    def set_platform(self, platform_name):
        self.platform = platform_name

    def change_rating_label(self):
        cat_txt = str(self.horizontalSlider_1.value()) + ' 星'
        self.label_set_rating.setText(cat_txt)

    def set_review(self):
        rating = self.horizontalSlider_1.value()
        text = self.plainTextEdit.toPlainText()
        db_update(rating, text, 'test_con', './ngrok.txt')

    def scrape_review(self):
        url = self.lineEdit.text()
        if url is None:
            raise Exception("Please")
        file_name = get_reviews(url, self.platform)
        if file_name is not None:
            db_upload_file(file_name, 'test_con', './ngrok.txt')


